var plotw = width-100; // set these up to explore changing the scale later.
var plotw2 = 100;
var ploth = height-100;
var ploth2 = 100;

var t_scale = d3.scale.linear().domain([500, 30000]).range([0, 2*Math.PI]),
    x = d3.scale.linear().domain([-2, 2]).range([plotw2, plotw]),
    y = d3.scale.linear().domain([-2, 2]).range([ploth, ploth2]);

var brush = svg.append('circle')
        .attr({r: 4}),
    previous = position(0);

var colordraw = 'red'; //initial color of drawing

function change_color(){
    if(colordraw == 'red'){
            colordraw ='orange';
            return;
        }

        if(colordraw == 'orange'){
            colordraw ='yellow';
            return;
        }
        if(colordraw =='yellow'){
            colordraw ='green';
            return;
        }
        if(colordraw =='green'){
            colordraw ='blue';
            return;
        }
        if(colordraw =='blue'){
            colordraw ='violet';
            return;
        }
        if(colordraw =='violet'){
            colordraw ='red';
            return;
        }
        return;

}

var step = function (time) {
    if (time &gt; t_scale.domain()[1]) {
        return true;
    }

    var t = t_scale(time),
        pos = position(t);
    divd.innerHTML = divd.innerHTML + ' '+t + ' '+ time;

        change_color();

        divd.innerHTML = divd.innerHTML + ' '+ colordraw;

    brush.attr({cx: x(pos.x),
                cy: y(pos.y)});
    svg.append('line')
        .attr({x1: x(previous.x),
               y1: y(previous.y),
               x2: x(pos.x),
               y2: y(pos.y),
               stroke: colordraw,
               'stroke-width': 1.3});

    previous = pos;
};

